func FuzzSniffQUIC(f *testing.F) {
	f.Fuzz(func(t *testing.T, data []byte) {
		var metadata adapter.InboundContext
		err := sniff.QUICClientHello(context.Background(), &metadata, data)
		require.Error(t, err)
	})
}
